import { Breadcrumb, Row, Container } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useRouter } from "next/router";

const parseText = (text) => {
  var displayedText = true;

  for (var x = 0; x < text.length; x++) {
    var c = text.charAt(x);

    if (c == "<") {
      displayedText = false;
    } else if (c == ">") {
      displayedText = true;
    }

    if (displayedText && c == "-") {
      text = text.substring(0, x) + " " + text.substring(x + 1, text.length);
    }
  }

  return text;
};

const ParseLink = (link) => {
  for (var x = 0; x < link.length; x++) {
    var c = link.charAt(x);

    if (c == "-") {
      link = link.substring(0, x) + "_" + link.substring(x + 1, link.length);
    }
  }
  return link;
};
const FoldersHeaderBar = () => {
  const router = useRouter();
  const items = router.asPath.split("/");
  return (
    <Container className="grey-box">
      <Row className="folders-navigation">
        <Breadcrumb>
          <Breadcrumb.Item href="/">
            <FontAwesomeIcon icon={"home"} />
          </Breadcrumb.Item>
          {items.map((item) => (
            <Breadcrumb.Item href={`/${ParseLink(item)}`}>
              {parseText(item)}
            </Breadcrumb.Item>
          ))}
        </Breadcrumb>
      </Row>
    </Container>
  );
};

export default FoldersHeaderBar;
