import { Row, Col } from "react-bootstrap";

class Tab extends React.Component {
  constructor(props) {
    super(props);
    this.state = { acvtive: this.props.highlighted };
  }

  activeToggle = (state) => {
    this.setState({ active: !state });
  };

  onClick = (event) => {
    this.props.onClick(event);
    this.activeToggle(this.state.active);
  };

  render() {
    return (
      <Col className="tab-col">
        <div
          onClick={this.onClick}
          id={this.props.id}
          className={this.state.active == true ? "tab active" : "tab"}
        >
          <h3 id={this.props.id}>{this.props.title}</h3>
        </div>
      </Col>
    );
  }
}

const Tabs = ({ onClick }) => {
  return (
    <Row>
      <Tab
        highlighted={true}
        onClick={onClick}
        id="do pobrania"
        title="Wnioski do pobrania"
      />
      <Tab
        onClick={onClick}
        id="interaktywne"
        title="Interaktywne formularze"
      />
    </Row>
  );
};

export default Tabs;
