import { Row, Col } from "react-bootstrap";

const Tab = (props) => {
  return (
    <Col className={props.active == true ? "tab-col active" : "tab-col"}>
      <div onClick={props.onClick} id={props.id}>
        <h3 id={props.id}>{props.title}</h3>
      </div>
    </Col>
  );
};

class Tabs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tab1: true,
      tab2: false,
    };
  }

  activeToggle = () => {
    const current_state = this.state;
    this.setState({ tab1: !current_state.tab1, tab2: !current_state.tab2 });
  };

  onClick = (event) => {
    this.props.onClick(event);
    if (event.target.className == "tab") {
      this.activeToggle(this.state.active);
    }
  };

  render() {
    return (
      <Row>
        <Tab
          active={this.state.tab1}
          onClick={this.onClick}
          id="do pobrania"
          title="Wnioski do pobrania"
        />
        <Tab
          active={this.state.tab2}
          onClick={this.onClick}
          id="interaktywne"
          title="Interaktywne formularze"
        />
      </Row>
    );
  }
}

export default Tabs;
