export function generateHref(items, item_index) {
  for (var i = 0; i < items.length; i++) {
    items[i] = convertPol(delParantheses(items[i]))
      .split(/[\s/]+/)
      .join(" ");
  }
  var Href = "";
  for (var i = 0; i < item_index + 1; i++) {
    Href = Href + "/" + items[i].split(" ").join("-").toLowerCase();
  }
  return Href;
}
