export default function validate(values) {
  let errors = {};
  const empty_field_error = "Pole nie może być puste";
  const date = values.birthDate.split("/");
  if (!values.surname) {
    errors.surname = empty_field_error;
  }
  if (!values.familyName) {
    errors.familyName = empty_field_error;
  }
  if (!values.name1) {
    errors.name1 = empty_field_error;
  }
  if (!values.fathersName) {
    errors.fathersName = empty_field_error;
  }
  if (!values.mothersName) {
    errors.mothersName = empty_field_error;
  }
  if (!values.mothersName) {
    errors.mothersName = empty_field_error;
  }
  if (!values.birthDate) {
    errors.birthDate = empty_field_error;
  } else if (values.birthDate.split("/")) return errors;
}
