import { delParantheses, convertPol } from "./charsConvertion";

export function convertCategory(category) {
  category = delParantheses(category);
  category = convertPol(category);
  category = category
    .split(/[\s/]+/)
    .join("-")
    .toLowerCase();

  return category;
}
