import { Breadcrumb, Row, Container } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useRouter } from "next/router";

const createItems = (router) => {
  var items = router.asPath.split("/");
  return items.map((item) => {
    return {
      item: {
        id: item.split("-").join(" "),
        link: router.pathname,
      },
    };
  });
};

const FoldersHeaderBar = () => {
  const router = useRouter();
  const items = createItems(router);
  return (
    <Container className="grey-box">
      <Row className="folders-navigation">
        <Breadcrumb>
          <Breadcrumb.Item href="/">
            <FontAwesomeIcon icon="home" />
          </Breadcrumb.Item>
          {items.map((item) => (
            <Breadcrumb.Item>{item}</Breadcrumb.Item>
          ))}
        </Breadcrumb>
      </Row>
    </Container>
  );
};

export default FoldersHeaderBar;
