export default function validate(values) {
  let errors = {};
  const empty_field_error = "Pole nie może być puste";
  const date = values.birthDate.split("-");
  date_year = date[0];
  date_year = date[1];
  date_year = date[2];
  const today = new Date();
  const actual_year = today.getFullYear();
  const actual_month = today.getMonth();
  const actual_day = today.getDate();

  for (var key in values) {
    if (values.hasOwnProperty(key)) {
      if (!values[key]) {
        errors[key] = empty_field_error;
      }
    }
  }
  return errors;
}
