import { Breadcrumb, Row, Container } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const generateHref = (items, item_index) => {
  var Href = "";
  for (var i = 0; i < item_index + 1; i++) {
    Href = Href + "/" + items[i].split(" ").join("-").toLowerCase();
  }
  return Href;
};

const convertPol = (text) => {
  for (var i = 0; i < text.length; i++) {
    var c = text.charAt(i);
    // NIE ZAMIENIA ZNAKU!!!!
    if (c == "b") {
      text = text.substring(0, i) + "" + text.substring(i + 1, text.length);
    }
    // if (c == "ł") {
    //   c = "l";
    // }
    // if (c == "ż" || c == "ź") {
    //   c = "z";
    // }
    // if (c == "ó") {
    //   c = "o";
    // }
    // if (c == "ą") {
    //   c = "a";
    // }
    // if (c == "ę") {
    //   c = "e";
    // }
    // if (c == "ć") {
    //   c = "c";
    // }
    // if (c == "ś") {
    //   c = "s";
    // }
  }
  return text;
};

const FoldersHeaderBar = ({ breadcrumb }) => {
  var items = breadcrumb.split("|");
  items.forEach(convertPol);
  console.log("Items: ", items);
  return (
    <Container className="grey-box">
      <Row className="folders-navigation">
        <Breadcrumb>
          <Breadcrumb.Item href="/">
            <FontAwesomeIcon icon="home" />
          </Breadcrumb.Item>
          {items.map((item) => (
            <Breadcrumb.Item href={generateHref(items, items.indexOf(item))}>
              {item}
            </Breadcrumb.Item>
          ))}
        </Breadcrumb>
      </Row>
    </Container>
  );
};

export default FoldersHeaderBar;
