export function delParantheses(text) {
  for (var i = 0; i < text.length; i++) {
    var c = text.charAt(i);

    if (c == "(") {
      text = text.substring(0, i) + "" + text.substring(i + 1, text.length);
    }
    if (c == ")") {
      text = text.substring(0, i) + "" + text.substring(i + 1, text.length);
    }
  }
  return text;
}

export function convertPol(text) {
  for (var i = 0; i < text.length; i++) {
    var c = text.charAt(i);

    if (c == "ń") {
      c = "n";
    }
    if (c == "ł") {
      c = "l";
    }
    if (c == "ż" || c == "ź") {
      c = "z";
    }
    if (c == "ó") {
      c = "o";
    }
    if (c == "ą") {
      c = "a";
    }
    if (c == "ć") {
      c = "c";
    }
    if (c == "ś") {
      c = "s";
    }
  }
  return text;
}
