import { Breadcrumb, Row, Container } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useRouter } from "next/router";

const parseText = (text) => {
  var displayedText = true;

  for (var x = 0; x < text.length; x++) {
    var c = text.charAt(x);

    if (c == "<") {
      displayedText = false;
    } else if (c == ">") {
      displayedText = true;
    }

    if (displayedText && c == "-") {
      text = text.substring(0, x) + " " + text.substring(x + 1, text.length);
    }
  }

  return text;
};

const FoldersHeaderBar = () => {
  const router = useRouter();
  const items = router.asPath.split("/");
  return (
    <Container className="grey-box">
      <Row className="folders-navigation">
        {items.map((item) => (
          <li>{item}</li>
        ))}
        <Breadcrumb>
          <Breadcrumb.Item href="/index">
            <FontAwesomeIcon icon={"home"} />
          </Breadcrumb.Item>
          {items.map((item) => (
            <Breadcrumb.Item href={item}>{parseText(item)}</Breadcrumb.Item>
          ))}
        </Breadcrumb>
      </Row>
    </Container>
  );
};

export default FoldersHeaderBar;
