import { Card } from "react-bootstrap";
import Link from "next/link";

const delParantheses = (text) => {
  for (var i = 0; i < text.length; i++) {
    var c = text.charAt(i);

    if (c == "(") {
      text = text.substring(0, i) + "" + text.substring(i + 1, text.length);
    }
    if (c == ")") {
      text = text.substring(0, i) + "" + text.substring(i + 1, text.length);
    }
  }
  return text;
};

const Folder = ({ title, options, category }) => {
  category = delParantheses(category);
  category = category
    .split(/[\s/]+/)
    .join("-")
    .toLowerCase();
  return (
    <Card className="folder-card">
      <div className="header">
        <h4>{title}</h4>
      </div>
      <ul>
        {options.map((option) => (
          <Link
            href={`/cudzoziemcy/${category}/[id]`}
            as={`/cudzoziemcy/${category}/${option
              .split(" ")
              .join("-")
              .toLowerCase()}`}
          >
            <li>
              <a className={option}>{option}</a>
              <hr className="folder-separator" />
            </li>
          </Link>
        ))}
      </ul>
    </Card>
  );
};

export default Folder;
