import { Breadcrumb, Row, Container } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { delParantheses, convertPol } from "../../../lib/charsConvertion";

const generateHref = (items, item_index) => {
  for (var i = 0; i < items.length; i++) {
    items[i].link = convertPol(delParantheses(items[i].link))
      .split(/[\s/]+/)
      .join(" ");
  }
  var Href = "";
  for (var i = 0; i < item_index + 1; i++) {
    Href = Href + "/" + items[i].link.split(" ").join("-").toLowerCase();
  }
  return Href;
};

const FoldersHeaderBar = ({ breadcrumb }) => {
  const items = breadcrumb.split("|");
  items.map((item) => {
    item = {
      name: item,
      link: item,
    };
  });
  return (
    <Container className="grey-box">
      <Row className="folders-navigation">
        <Breadcrumb>
          <Breadcrumb.Item href="/">
            <FontAwesomeIcon icon="home" />
          </Breadcrumb.Item>
          {items.map((item) => (
            <Breadcrumb.Item href={generateHref(items, items.indexOf(item))}>
              {item.name}
            </Breadcrumb.Item>
          ))}
        </Breadcrumb>
      </Row>
    </Container>
  );
};

export default FoldersHeaderBar;
